		echo $o['error'];
		echo '<a href="tb_contratacao_view.php?SelectedID='.urlencode($selected_id)."\">{$Translation['< back']}</a>";
		exit;
	}


	// hook: tb_contratacao_after_update
	if(function_exists('tb_contratacao_after_update')){
		$res = sql("SELECT * FROM `tb_contratacao` WHERE `id`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)){
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = $data['id'];
		$args = array();
		if(!tb_contratacao_after_update($data, getMemberInfo(), $args)){ return; }
	}

	// mm: update ownership data
	sql("update membership_userrecords set dateUpdated='".time()."' where tableName='tb_contratacao' and pkValue='".makeSafe($selected_id)."'", $eo);

}

