		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">" . $Translation['error:'] . " 'Nome': " . $Translation['field not null'] . '<br><br>';
		echo '<a href="" onclick="history.go(-1); return false;">'.$Translation['< back'].'</a></div>';
		exit;
	}
	if($data['dta_inicio']== ''){
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">" . $Translation['error:'] . " 'Data de in&#237;cio': " . $Translation['field not null'] . '<br><br>';
		echo '<a href="" onclick="history.go(-1); return false;">'.$Translation['< back'].'</a></div>';
		exit;
	}

	/* for empty upload fields, when saving a copy of an existing record, copy the original upload field */
	if($_REQUEST['SelectedID']){
		$res = sql("select * from tb_contrato where id='" . makeSafe($_REQUEST['SelectedID']) . "'", $eo);
		if($row = db_fetch_assoc($res)){
			if(!$data['str_anexo']) $data['str_anexo'] = makeSafe($row['str_anexo']);
		}
	}

	// hook: tb_contrato_before_insert
	if(function_exists('tb_contrato_before_insert')){
		$args=array();
		if(!tb_contrato_before_insert($data, getMemberInfo(), $args)){ return false; }
	}

	$o = array('silentErrors' => true);
	sql('insert into `tb_contrato` set       `empresa_id`=' . (($data['empresa_id'] !== '' && $data['empresa_id'] !== NULL) ? "'{$data['empresa_id']}'" : 'NULL') . ', `str_nome`=' . (($data['str_nome'] !== '' && $data['str_nome'] !== NULL) ? "'{$data['str_nome']}'" : 'NULL') . ', `dta_inicio`=' . (($data['dta_inicio'] !== '' && $data['dta_inicio'] !== NULL) ? "'{$data['dta_inicio']}'" : 'NULL') . ', `dta_termino`=' . (($data['dta_termino'] !== '' && $data['dta_termino'] !== NULL) ? "'{$data['dta_termino']}'" : 'NULL') . ', `str_detalhes`=' . (($data['str_detalhes'] !== '' && $data['str_detalhes'] !== NULL) ? "'{$data['str_detalhes']}'" : 'NULL') . ', ' . ($data['str_anexo'] != '' ? "`str_anexo`='{$data['str_anexo']}'" : '`str_anexo`=NULL') . ', `bol_status`=' . (($data['bol_status'] !== '' && $data['bol_status'] !== NULL) ? "'{$data['bol_status']}'" : 'NULL'), $o);
	if($o['error']!=''){
		echo $o['error'];
		echo "<a href=\"tb_contrato_view.php?addNew_x=1\">{$Translation['< back']}</a>";
		exit;
	}

	$recID = db_insert_id(db_link());

	// hook: tb_contrato_after_insert
	if(function_exists('tb_contrato_after_insert')){
		$res = sql("select * from `tb_contrato` where `id`='" . makeSafe($recID, false) . "' limit 1", $eo);
		if($row = db_fetch_assoc($res)){
