	$data['dta_termino'] = parseMySQLDate($data['dta_termino'], '');
	$data['str_detalhes'] = br2nl(makeSafe($_REQUEST['str_detalhes']));
	$data['bol_status'] = makeSafe($_REQUEST['bol_status']);
		if($data['bol_status'] == empty_lookup_value){ $data['bol_status'] = ''; }
	$data['selectedID']=makeSafe($selected_id);
	if($_REQUEST['str_anexo_remove'] == 1){
		$data['str_anexo'] = '';
