		$res = sql("select `str_anexo` from `tb_contrato` where `id`='".makeSafe($selected_id)."'", $eo);
		if($row=@db_fetch_row($res)){
			if($row[0]!=''){
				@unlink(getUploadDir('').$row[0]);
			}
		}
	}else{
		$data['str_anexo'] = PrepareUploadedFile('str_anexo', 102400, 'txt|doc|docx|docm|odt|pdf|rtf', false, "");
		// delete file from server
		if($data['str_anexo'] != ''){
			$res = sql("select `str_anexo` from `tb_contrato` where `id`='".makeSafe($selected_id)."'", $eo);
			if($row=@db_fetch_row($res)){
				if($row[0]!=''){
					@unlink(getUploadDir('').$row[0]);
				}
			}
		}
	}

	// hook: tb_contrato_before_update
	if(function_exists('tb_contrato_before_update')){
		$args=array();
		if(!tb_contrato_before_update($data, getMemberInfo(), $args)){ return false; }
	}

	$o=array('silentErrors' => true);
	sql('update `tb_contrato` set       `empresa_id`=' . (($data['empresa_id'] !== '' && $data['empresa_id'] !== NULL) ? "'{$data['empresa_id']}'" : 'NULL') . ', `str_nome`=' . (($data['str_nome'] !== '' && $data['str_nome'] !== NULL) ? "'{$data['str_nome']}'" : 'NULL') . ', `dta_inicio`=' . (($data['dta_inicio'] !== '' && $data['dta_inicio'] !== NULL) ? "'{$data['dta_inicio']}'" : 'NULL') . ', `dta_termino`=' . (($data['dta_termino'] !== '' && $data['dta_termino'] !== NULL) ? "'{$data['dta_termino']}'" : 'NULL') . ', `str_detalhes`=' . (($data['str_detalhes'] !== '' && $data['str_detalhes'] !== NULL) ? "'{$data['str_detalhes']}'" : 'NULL') . ', ' . ($data['str_anexo']!='' ? "`str_anexo`='{$data['str_anexo']}'" : ($_REQUEST['str_anexo_remove'] != 1 ? '`str_anexo`=`str_anexo`' : '`str_anexo`=NULL')) . ', `bol_status`=' . (($data['bol_status'] !== '' && $data['bol_status'] !== NULL) ? "'{$data['bol_status']}'" : 'NULL') . " where `id`='".makeSafe($selected_id)."'", $o);
	if($o['error']!=''){
		echo $o['error'];
		echo '<a href="tb_contrato_view.php?SelectedID='.urlencode($selected_id)."\">{$Translation['< back']}</a>";
		exit;
	}


	// hook: tb_contrato_after_update
	if(function_exists('tb_contrato_after_update')){
		$res = sql("SELECT * FROM `tb_contrato` WHERE `id`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)){
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = $data['id'];
		$args = array();
		if(!tb_contrato_after_update($data, getMemberInfo(), $args)){ return; }
	}

	// mm: update ownership data
	sql("update membership_userrecords set dateUpdated='".time()."' where tableName='tb_contrato' and pkValue='".makeSafe($selected_id)."'", $eo);

}

