	$templateCode = str_replace('<%%DETAIL_VIEW_TITLE%%>', 'Detalhes do contrato', $templateCode);
	$templateCode = str_replace('<%%RND1%%>', $rnd1, $templateCode);
	$templateCode = str_replace('<%%EMBEDDED%%>', ($_REQUEST['Embedded'] ? 'Embedded=1' : ''), $templateCode);
	// process buttons
	if($AllowInsert){
		if(!$selected_id) $templateCode = str_replace('<%%INSERT_BUTTON%%>', '<button type="submit" class="btn btn-success" id="insert" name="insert_x" value="1" onclick="return tb_contrato_validateData();"><i class="glyphicon glyphicon-plus-sign"></i> ' . $Translation['Save New'] . '</button>', $templateCode);
		$templateCode = str_replace('<%%INSERT_BUTTON%%>', '<button type="submit" class="btn btn-default" id="insert" name="insert_x" value="1" onclick="return tb_contrato_validateData();"><i class="glyphicon glyphicon-plus-sign"></i> ' . $Translation['Save As Copy'] . '</button>', $templateCode);
	}else{
		$templateCode = str_replace('<%%INSERT_BUTTON%%>', '', $templateCode);
	}

	// 'Back' button action
	if($_REQUEST['Embedded']){
		$backAction = 'AppGini.closeParentModal(); return false;';
	}else{
		$backAction = '$j(\'form\').eq(0).attr(\'novalidate\', \'novalidate\'); document.myform.reset(); return true;';
	}

	if($selected_id){
		if(!$_REQUEST['Embedded']) $templateCode = str_replace('<%%DVPRINT_BUTTON%%>', '<button type="submit" class="btn btn-default" id="dvprint" name="dvprint_x" value="1" onclick="$$(\'form\')[0].writeAttribute(\'novalidate\', \'novalidate\'); document.myform.reset(); return true;" title="' . html_attr($Translation['Print Preview']) . '"><i class="glyphicon glyphicon-print"></i> ' . $Translation['Print Preview'] . '</button>', $templateCode);
		if($AllowUpdate){
			$templateCode = str_replace('<%%UPDATE_BUTTON%%>', '<button type="submit" class="btn btn-success btn-lg" id="update" name="update_x" value="1" onclick="return tb_contrato_validateData();" title="' . html_attr($Translation['Save Changes']) . '"><i class="glyphicon glyphicon-ok"></i> ' . $Translation['Save Changes'] . '</button>', $templateCode);
		}else{
			$templateCode = str_replace('<%%UPDATE_BUTTON%%>', '', $templateCode);
		}
		if(($arrPerm[4]==1 && $ownerMemberID==getLoggedMemberID()) || ($arrPerm[4]==2 && $ownerGroupID==getLoggedGroupID()) || $arrPerm[4]==3){ // allow delete?
			$templateCode = str_replace('<%%DELETE_BUTTON%%>', '<button type="submit" class="btn btn-danger" id="delete" name="delete_x" value="1" onclick="return confirm(\'' . $Translation['are you sure?'] . '\');" title="' . html_attr($Translation['Delete']) . '"><i class="glyphicon glyphicon-trash"></i> ' . $Translation['Delete'] . '</button>', $templateCode);
		}else{
			$templateCode = str_replace('<%%DELETE_BUTTON%%>', '', $templateCode);
		}
		$templateCode = str_replace('<%%DESELECT_BUTTON%%>', '<button type="submit" class="btn btn-default" id="deselect" name="deselect_x" value="1" onclick="' . $backAction . '" title="' . html_attr($Translation['Back']) . '"><i class="glyphicon glyphicon-chevron-left"></i> ' . $Translation['Back'] . '</button>', $templateCode);
	}else{
		$templateCode = str_replace('<%%UPDATE_BUTTON%%>', '', $templateCode);
		$templateCode = str_replace('<%%DELETE_BUTTON%%>', '', $templateCode);
		$templateCode = str_replace('<%%DESELECT_BUTTON%%>', ($ShowCancel ? '<button type="submit" class="btn btn-default" id="deselect" name="deselect_x" value="1" onclick="' . $backAction . '" title="' . html_attr($Translation['Back']) . '"><i class="glyphicon glyphicon-chevron-left"></i> ' . $Translation['Back'] . '</button>' : ''), $templateCode);
	}

	// set records to read only if user can't insert new records and can't edit current record
	if(($selected_id && !$AllowUpdate && !$AllowInsert) || (!$selected_id && !$AllowInsert)){
		$jsReadOnly .= "\tjQuery('#empresa_id').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\tjQuery('#empresa_id_caption').prop('disabled', true).css({ color: '#555', backgroundColor: 'white' });\n";
		$jsReadOnly .= "\tjQuery('#str_nome').replaceWith('<div class=\"form-control-static\" id=\"str_nome\">' + (jQuery('#str_nome').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#dta_inicio').prop('readonly', true);\n";
		$jsReadOnly .= "\tjQuery('#dta_inicioDay, #dta_inicioMonth, #dta_inicioYear').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\tjQuery('#dta_termino').prop('readonly', true);\n";
		$jsReadOnly .= "\tjQuery('#dta_terminoDay, #dta_terminoMonth, #dta_terminoYear').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\tjQuery('#str_detalhes').replaceWith('<div class=\"form-control-static\" id=\"str_detalhes\">' + (jQuery('#str_detalhes').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#str_anexo').replaceWith('<div class=\"form-control-static\" id=\"str_anexo\">' + (jQuery('#str_anexo').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#str_anexo, #str_anexo-edit-link').hide();\n";
		$jsReadOnly .= "\tjQuery('#bol_status').prop('disabled', true);\n";
		$jsReadOnly .= "\tjQuery('.select2-container').hide();\n";

		$noUploads = true;
	}elseif($AllowInsert){
		$jsEditable .= "\tjQuery('form').eq(0).data('already_changed', true);"; // temporarily disable form change handler
			$jsEditable .= "\tjQuery('form').eq(0).data('already_changed', false);"; // re-enable form change handler
	}

	// process combos
	$templateCode = str_replace('<%%COMBO(empresa_id)%%>', $combo_empresa_id->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(empresa_id)%%>', $combo_empresa_id->MatchText, $templateCode);
	$templateCode = str_replace('<%%URLCOMBOTEXT(empresa_id)%%>', urlencode($combo_empresa_id->MatchText), $templateCode);
	$templateCode = str_replace('<%%COMBO(dta_inicio)%%>', ($selected_id && !$arrPerm[3] ? '<div class="form-control-static">' . $combo_dta_inicio->GetHTML(true) . '</div>' : $combo_dta_inicio->GetHTML()), $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(dta_inicio)%%>', $combo_dta_inicio->GetHTML(true), $templateCode);
	$templateCode = str_replace('<%%COMBO(dta_termino)%%>', ($selected_id && !$arrPerm[3] ? '<div class="form-control-static">' . $combo_dta_termino->GetHTML(true) . '</div>' : $combo_dta_termino->GetHTML()), $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(dta_termino)%%>', $combo_dta_termino->GetHTML(true), $templateCode);

	/* lookup fields array: 'lookup field name' => array('parent table name', 'lookup field caption') */
	$lookup_fields = array(  'empresa_id' => array('tb_empresa', 'Conta'));
	foreach($lookup_fields as $luf => $ptfc){
		$pt_perm = getTablePermissions($ptfc[0]);

		// process foreign key links
		if($pt_perm['view'] || $pt_perm['edit']){
			$templateCode = str_replace("<%%PLINK({$luf})%%>", '<button type="button" class="btn btn-default view_parent hspacer-md" id="' . $ptfc[0] . '_view_parent" title="' . html_attr($Translation['View'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-eye-open"></i></button>', $templateCode);
		}

		// if user has insert permission to parent table of a lookup field, put an add new button
		if($pt_perm['insert'] && !$_REQUEST['Embedded']){
			$templateCode = str_replace("<%%ADDNEW({$ptfc[0]})%%>", '<button type="button" class="btn btn-success add_new_parent hspacer-md" id="' . $ptfc[0] . '_add_new" title="' . html_attr($Translation['Add New'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-plus-sign"></i></button>', $templateCode);
		}
	}

	// process images
	$templateCode = str_replace('<%%UPLOADFILE(id)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(empresa_id)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(str_nome)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(dta_inicio)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(dta_termino)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(str_detalhes)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(str_anexo)%%>', ($noUploads ? '' : '<input type=hidden name=MAX_FILE_SIZE value=102400>'.$Translation['upload image'].' <input type="file" name="str_anexo" id="str_anexo">'), $templateCode);
	if($AllowUpdate && $row['str_anexo'] != ''){
		$templateCode = str_replace('<%%REMOVEFILE(str_anexo)%%>', '<br><input type="checkbox" name="str_anexo_remove" id="str_anexo_remove" value="1"> <label for="str_anexo_remove" style="color: red; font-weight: bold;">'.$Translation['remove image'].'</label>', $templateCode);
	}else{
		$templateCode = str_replace('<%%REMOVEFILE(str_anexo)%%>', '', $templateCode);
	}
	$templateCode = str_replace('<%%UPLOADFILE(bol_status)%%>', '', $templateCode);

	// process values
	if($selected_id){
		if( $dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', safe_html($urow['id']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', html_attr($row['id']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode($urow['id']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(empresa_id)%%>', safe_html($urow['empresa_id']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(empresa_id)%%>', html_attr($row['empresa_id']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(empresa_id)%%>', urlencode($urow['empresa_id']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(str_nome)%%>', safe_html($urow['str_nome']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(str_nome)%%>', html_attr($row['str_nome']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(str_nome)%%>', urlencode($urow['str_nome']), $templateCode);
		$templateCode = str_replace('<%%VALUE(dta_inicio)%%>', @date('d/m/Y', @strtotime(html_attr($row['dta_inicio']))), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(dta_inicio)%%>', urlencode(@date('d/m/Y', @strtotime(html_attr($urow['dta_inicio'])))), $templateCode);
		$templateCode = str_replace('<%%VALUE(dta_termino)%%>', @date('d/m/Y', @strtotime(html_attr($row['dta_termino']))), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(dta_termino)%%>', urlencode(@date('d/m/Y', @strtotime(html_attr($urow['dta_termino'])))), $templateCode);
		if($dvprint || (!$AllowUpdate && !$AllowInsert)){
			$templateCode = str_replace('<%%VALUE(str_detalhes)%%>', safe_html($urow['str_detalhes']), $templateCode);
		}else{
			$templateCode = str_replace('<%%VALUE(str_detalhes)%%>', html_attr($row['str_detalhes']), $templateCode);
		}
		$templateCode = str_replace('<%%URLVALUE(str_detalhes)%%>', urlencode($urow['str_detalhes']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(str_anexo)%%>', safe_html($urow['str_anexo']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(str_anexo)%%>', html_attr($row['str_anexo']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(str_anexo)%%>', urlencode($urow['str_anexo']), $templateCode);
		$templateCode = str_replace('<%%CHECKED(bol_status)%%>', ($row['bol_status'] ? "checked" : ""), $templateCode);
	}else{
		$templateCode = str_replace('<%%VALUE(id)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(empresa_id)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(empresa_id)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(str_nome)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(str_nome)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(dta_inicio)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(dta_inicio)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(dta_termino)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(dta_termino)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(str_detalhes)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(str_detalhes)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(str_anexo)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(str_anexo)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%CHECKED(bol_status)%%>', 'checked', $templateCode);
	}

	// process translations
	foreach($Translation as $symbol=>$trans){
		$templateCode = str_replace("<%%TRANSLATION($symbol)%%>", $trans, $templateCode);
	}

	// clear scrap
	$templateCode = str_replace('<%%', '<!-- ', $templateCode);
	$templateCode = str_replace('%%>', ' -->', $templateCode);

	// hide links to inaccessible tables
	if($_REQUEST['dvprint_x'] == ''){
		$templateCode .= "\n\n<script>\$j(function(){\n";
		$arrTables = getTableList();
		foreach($arrTables as $name => $caption){
			$templateCode .= "\t\$j('#{$name}_link').removeClass('hidden');\n";
			$templateCode .= "\t\$j('#xs_{$name}_link').removeClass('hidden');\n";
		}

		$templateCode .= $jsReadOnly;
		$templateCode .= $jsEditable;

		if(!$selected_id){
			$templateCode.="\n\tif(document.getElementById('str_anexoEdit')){ document.getElementById('str_anexoEdit').style.display='inline'; }";
			$templateCode.="\n\tif(document.getElementById('str_anexoEditLink')){ document.getElementById('str_anexoEditLink').style.display='none'; }";
		}

		$templateCode.="\n});</script>\n";
	}

	// ajaxed auto-fill fields
	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields

	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('tb_contrato');
	if($selected_id){
		$jdata = get_joined_record('tb_contrato', $selected_id);
		if($jdata === false) $jdata = get_defaults('tb_contrato');
		$rdata = $row;
	}
	$templateCode .= loadView('tb_contrato-ajax-cache', array('rdata' => $rdata, 'jdata' => $jdata));

	// hook: tb_contrato_dv
	if(function_exists('tb_contrato_dv')){
		$args=array();
		tb_contrato_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}
?>