	$ownerGroupID=sqlValue("select groupID from membership_userrecords where tableName='tb_entrevista' and pkValue='$selected_id'");
	$ownerMemberID=sqlValue("select lcase(memberID) from membership_userrecords where tableName='tb_entrevista' and pkValue='$selected_id'");
	if(($arrPerm[4]==1 && $ownerMemberID==getLoggedMemberID()) || ($arrPerm[4]==2 && $ownerGroupID==getLoggedGroupID()) || $arrPerm[4]==3){ // allow delete?
		// delete allowed, so continue ...
	}else{
		return $Translation['You don\'t have enough permissions to delete this record'];
	}

	// hook: tb_entrevista_before_delete
	if(function_exists('tb_entrevista_before_delete')){
		$args=array();
		if(!tb_entrevista_before_delete($selected_id, $skipChecks, getMemberInfo(), $args))
			return $Translation['Couldn\'t delete this record'];
	}

	sql("delete from `tb_entrevista` where `id`='$selected_id'", $eo);

	// hook: tb_entrevista_after_delete
	if(function_exists('tb_entrevista_after_delete')){
		$args=array();
		tb_entrevista_after_delete($selected_id, getMemberInfo(), $args);
	}

	// mm: delete ownership data
	sql("delete from membership_userrecords where tableName='tb_entrevista' and pkValue='$selected_id'", $eo);
}

