		$templateCode = str_replace('<%%DESELECT_BUTTON%%>', '<button type="submit" class="btn btn-default" id="deselect" name="deselect_x" value="1" onclick="' . $backAction . '" title="' . html_attr($Translation['Back']) . '"><i class="glyphicon glyphicon-chevron-left"></i> ' . $Translation['Back'] . '</button>', $templateCode);
	}else{
		$templateCode = str_replace('<%%UPDATE_BUTTON%%>', '', $templateCode);
		$templateCode = str_replace('<%%DELETE_BUTTON%%>', '', $templateCode);
		$templateCode = str_replace('<%%DESELECT_BUTTON%%>', ($ShowCancel ? '<button type="submit" class="btn btn-default" id="deselect" name="deselect_x" value="1" onclick="' . $backAction . '" title="' . html_attr($Translation['Back']) . '"><i class="glyphicon glyphicon-chevron-left"></i> ' . $Translation['Back'] . '</button>' : ''), $templateCode);
	}

	// set records to read only if user can't insert new records and can't edit current record
	if(($selected_id && !$AllowUpdate && !$AllowInsert) || (!$selected_id && !$AllowInsert)){
		$jsReadOnly .= "\tjQuery('#empresa_id').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\tjQuery('#empresa_id_caption').prop('disabled', true).css({ color: '#555', backgroundColor: 'white' });\n";
		$jsReadOnly .= "\tjQuery('#dta_emissao').prop('readonly', true);\n";
		$jsReadOnly .= "\tjQuery('#dta_emissaoDay, #dta_emissaoMonth, #dta_emissaoYear').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\tjQuery('#dta_competencia').prop('readonly', true);\n";
		$jsReadOnly .= "\tjQuery('#dta_competenciaDay, #dta_competenciaMonth, #dta_competenciaYear').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\tjQuery('#flo_valor').replaceWith('<div class=\"form-control-static\" id=\"flo_valor\">' + (jQuery('#flo_valor').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#int_nf').replaceWith('<div class=\"form-control-static\" id=\"int_nf\">' + (jQuery('#int_nf').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#str_observacoes').replaceWith('<div class=\"form-control-static\" id=\"str_observacoes\">' + (jQuery('#str_observacoes').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('.select2-container').hide();\n";

		$noUploads = true;
	}elseif($AllowInsert){
		$jsEditable .= "\tjQuery('form').eq(0).data('already_changed', true);"; // temporarily disable form change handler
			$jsEditable .= "\tjQuery('form').eq(0).data('already_changed', false);"; // re-enable form change handler
	}

	// process combos
	$templateCode = str_replace('<%%COMBO(empresa_id)%%>', $combo_empresa_id->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(empresa_id)%%>', $combo_empresa_id->MatchText, $templateCode);
	$templateCode = str_replace('<%%URLCOMBOTEXT(empresa_id)%%>', urlencode($combo_empresa_id->MatchText), $templateCode);
	$templateCode = str_replace('<%%COMBO(dta_emissao)%%>', ($selected_id && !$arrPerm[3] ? '<div class="form-control-static">' . $combo_dta_emissao->GetHTML(true) . '</div>' : $combo_dta_emissao->GetHTML()), $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(dta_emissao)%%>', $combo_dta_emissao->GetHTML(true), $templateCode);
	$templateCode = str_replace('<%%COMBO(dta_competencia)%%>', ($selected_id && !$arrPerm[3] ? '<div class="form-control-static">' . $combo_dta_competencia->GetHTML(true) . '</div>' : $combo_dta_competencia->GetHTML()), $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(dta_competencia)%%>', $combo_dta_competencia->GetHTML(true), $templateCode);

	/* lookup fields array: 'lookup field name' => array('parent table name', 'lookup field caption') */
	$lookup_fields = array(  'empresa_id' => array('tb_empresa', 'Conta'));
	foreach($lookup_fields as $luf => $ptfc){
		$pt_perm = getTablePermissions($ptfc[0]);

		// process foreign key links
		if($pt_perm['view'] || $pt_perm['edit']){
			$templateCode = str_replace("<%%PLINK({$luf})%%>", '<button type="button" class="btn btn-default view_parent hspacer-md" id="' . $ptfc[0] . '_view_parent" title="' . html_attr($Translation['View'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-eye-open"></i></button>', $templateCode);
		}

		// if user has insert permission to parent table of a lookup field, put an add new button
		if($pt_perm['insert'] && !$_REQUEST['Embedded']){
			$templateCode = str_replace("<%%ADDNEW({$ptfc[0]})%%>", '<button type="button" class="btn btn-success add_new_parent hspacer-md" id="' . $ptfc[0] . '_add_new" title="' . html_attr($Translation['Add New'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-plus-sign"></i></button>', $templateCode);
		}
	}

	// process images
	$templateCode = str_replace('<%%UPLOADFILE(id)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(empresa_id)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(dta_emissao)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(dta_competencia)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(flo_valor)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(int_nf)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(str_observacoes)%%>', '', $templateCode);

	// process values
	if($selected_id){
		if( $dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', safe_html($urow['id']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', html_attr($row['id']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode($urow['id']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(empresa_id)%%>', safe_html($urow['empresa_id']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(empresa_id)%%>', html_attr($row['empresa_id']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(empresa_id)%%>', urlencode($urow['empresa_id']), $templateCode);
		$templateCode = str_replace('<%%VALUE(dta_emissao)%%>', @date('d/m/Y', @strtotime(html_attr($row['dta_emissao']))), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(dta_emissao)%%>', urlencode(@date('d/m/Y', @strtotime(html_attr($urow['dta_emissao'])))), $templateCode);
		$templateCode = str_replace('<%%VALUE(dta_competencia)%%>', @date('d/m/Y', @strtotime(html_attr($row['dta_competencia']))), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(dta_competencia)%%>', urlencode(@date('d/m/Y', @strtotime(html_attr($urow['dta_competencia'])))), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(flo_valor)%%>', safe_html($urow['flo_valor']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(flo_valor)%%>', html_attr($row['flo_valor']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(flo_valor)%%>', urlencode($urow['flo_valor']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(int_nf)%%>', safe_html($urow['int_nf']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(int_nf)%%>', html_attr($row['int_nf']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(int_nf)%%>', urlencode($urow['int_nf']), $templateCode);
		if($dvprint || (!$AllowUpdate && !$AllowInsert)){
			$templateCode = str_replace('<%%VALUE(str_observacoes)%%>', safe_html($urow['str_observacoes']), $templateCode);
		}else{
			$templateCode = str_replace('<%%VALUE(str_observacoes)%%>', html_attr($row['str_observacoes']), $templateCode);
		}
		$templateCode = str_replace('<%%URLVALUE(str_observacoes)%%>', urlencode($urow['str_observacoes']), $templateCode);
	}else{
		$templateCode = str_replace('<%%VALUE(id)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(empresa_id)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(empresa_id)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(dta_emissao)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(dta_emissao)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(dta_competencia)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(dta_competencia)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(flo_valor)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(flo_valor)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(int_nf)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(int_nf)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(str_observacoes)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(str_observacoes)%%>', urlencode(''), $templateCode);
	}

	// process translations
	foreach($Translation as $symbol=>$trans){
		$templateCode = str_replace("<%%TRANSLATION($symbol)%%>", $trans, $templateCode);
	}

	// clear scrap
	$templateCode = str_replace('<%%', '<!-- ', $templateCode);
	$templateCode = str_replace('%%>', ' -->', $templateCode);

	// hide links to inaccessible tables
	if($_REQUEST['dvprint_x'] == ''){
		$templateCode .= "\n\n<script>\$j(function(){\n";
		$arrTables = getTableList();
		foreach($arrTables as $name => $caption){
			$templateCode .= "\t\$j('#{$name}_link').removeClass('hidden');\n";
			$templateCode .= "\t\$j('#xs_{$name}_link').removeClass('hidden');\n";
		}

		$templateCode .= $jsReadOnly;
		$templateCode .= $jsEditable;

		if(!$selected_id){
		}

		$templateCode.="\n});</script>\n";
	}

	// ajaxed auto-fill fields
	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields

	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('tb_fatura');
	if($selected_id){
		$jdata = get_joined_record('tb_fatura', $selected_id);
		if($jdata === false) $jdata = get_defaults('tb_fatura');
		$rdata = $row;
	}
	$templateCode .= loadView('tb_fatura-ajax-cache', array('rdata' => $rdata, 'jdata' => $jdata));

	// hook: tb_fatura_dv
	if(function_exists('tb_fatura_dv')){
		$args=array();
		tb_fatura_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}
?>