		if( $dvprint) $templateCode = str_replace('<%%VALUE(str_unidade_medida)%%>', safe_html($urow['str_unidade_medida']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(str_unidade_medida)%%>', html_attr($row['str_unidade_medida']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(str_unidade_medida)%%>', urlencode($urow['str_unidade_medida']), $templateCode);
	}else{
		$templateCode = str_replace('<%%VALUE(id)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(str_departamento)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(str_departamento)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(str_nome)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(str_nome)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(str_descricao)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(str_descricao)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(str_unidade_medida)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(str_unidade_medida)%%>', urlencode(''), $templateCode);
	}

	// process translations
	foreach($Translation as $symbol=>$trans){
		$templateCode = str_replace("<%%TRANSLATION($symbol)%%>", $trans, $templateCode);
	}

	// clear scrap
	$templateCode = str_replace('<%%', '<!-- ', $templateCode);
	$templateCode = str_replace('%%>', ' -->', $templateCode);

	// hide links to inaccessible tables
	if($_REQUEST['dvprint_x'] == ''){
		$templateCode .= "\n\n<script>\$j(function(){\n";
		$arrTables = getTableList();
		foreach($arrTables as $name => $caption){
			$templateCode .= "\t\$j('#{$name}_link').removeClass('hidden');\n";
			$templateCode .= "\t\$j('#xs_{$name}_link').removeClass('hidden');\n";
		}

		$templateCode .= $jsReadOnly;
		$templateCode .= $jsEditable;

		if(!$selected_id){
		}

		$templateCode.="\n});</script>\n";
	}

	// ajaxed auto-fill fields
	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields

	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('tb_indicador');
	if($selected_id){
		$jdata = get_joined_record('tb_indicador', $selected_id);
		if($jdata === false) $jdata = get_defaults('tb_indicador');
		$rdata = $row;
	}
	$templateCode .= loadView('tb_indicador-ajax-cache', array('rdata' => $rdata, 'jdata' => $jdata));

	// hook: tb_indicador_dv
	if(function_exists('tb_indicador_dv')){
		$args=array();
		tb_indicador_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}
